import React from 'react'
import HeadingBanner from './HeadingBanner';
import Blog from './Blog';
import Genres from './Genres';
import pen from './pen.png'
import Footer from './Footer';
import InputModal from './InputModal';
class Blog_Container extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
           modal:false
        };
    }
    showModal = () => {
        this.setState({ modal: true });
    };

    hideModal = () => {
        this.setState({ modal: false });
    };

    render() {
        return (
            <div>
                <HeadingBanner />
                <div className="container">
                    <div className="row my-5">
                        <div className="col-lg-8 col-sm-12">
                            <Blog genre={"Tour"} date={"20/8/19"} author={"Kanishka"} title={"Delhi Tour"} desc={"Delhi has one of the best street foods I have ever tasted, it is mouth watering. Chandi Chowk is a must visit for Chicken lovers.Delhi has one of the best street foods I have ever tasted, it is mouth watering. Chandi Chowk is a must visit for Chicken lovers.Delhi has one of the best street foods I have ever tasted, it is mouth watering. Chandi Chowk is a must visit for Chicken lovers.Delhi has one of the best street foods I have ever tasted, it is mouth watering. Chandi Chowk is a must visit for Chicken lovers.Delhi has one of the best street foods I have ever tasted, it is mouth watering. Chandi Chowk is a must visit for Chicken lovers."}/>
                            <Blog genre={"Tour"} date={"20/8/19"} author={"Kanishka"} title={"Delhi Tour"} desc={"Delhi has one of the best street foods I have ever tasted, it is mouth watering. Chandi Chowk is a must visit for Chicken lovers."}/>
                            <Blog genre={"Tour"} date={"20/8/19"} author={"Kanishka"} title={"Delhi Tour"} desc={"Delhi has one of the best street foods I have ever tasted, it is mouth watering. Chandi Chowk is a must visit for Chicken lovers."}/>
                        </div>

                        <div className="col-lg-4 col-sm-12">
                            <div className="widget" style={{textAlign:"center"}}>
                                <button className="start_button" onClick={this.showModal}>Start Writing...</button>
                                <img src={pen} alt=""/>
                            </div>
                            <Genres genres={["Tour","Comedy","Horror","Romance"]}/>
                        </div>
                    </div>  
                    <InputModal modal={this.state.modal} handleClose={this.hideModal}/>
                </div>

                <Footer />
            </div>

        );
    }
}

export default Blog_Container;
