import React from 'react';
import './App.css';
import BlogContainer from './components/BlogContainer';
import LoginForm from './components/LoginForm';

class App extends React.Component {
    state = {
        name:"",
        email:"",
        passsword:"",
        address:"",
        route:"login"
    }
    onChange = (e) => {
        console.log(e.target.name)
        this.setState({
            [e.target.name]:e.target.value
        });
        console.log(this.state.email)
    };

    authenticate = (admin) => {
        console.log("admin authentication",this.state.email)
       if(admin){
        console.log("admin authentication",this.state)
        if(this.state.email==="admin@mail.com" && this.state.passsword==="admin"){
            this.setState({route:"admin"})
        }
       }
       else{
           this.setState({route:"blog"})
       }
    }
  render(){
    return (
        <div className="App">
            {
                this.state.route==="admin"?
                <BlogContainer />:<LoginForm onChange={this.onChange} onSubmit={this.onSubmit} authenticate={this.authenticate}/>

            }


        </div>
      );
  } 
}

export default App;
