import React from 'react';

class InputModal extends React.Component{
    state={
        modal:false,
        name:" ",
        email:" ",
        count:" "
    };

    toggle = () => {
        this.setState({
          modal: !this.state.modal
        });
      };

    onChangeName = (e) => {
        this.setState({
            name:e.target.value
        });
    };

    onChangeEmail = (e) => {
        this.setState({
            email:e.target.value
        });
    };

    onChangeCount = (e) => {
        this.setState({
            count:e.target.value
        });
    };

    onSubmit = (e) => {
        e.preventDefault();

        const newItem = {
            name:this.state.name,
            email:this.state.email,
            count:this.state.count
        };

        // this.props.addItem(newItem);      //calling the action

        this.toggle();
    };

    render(){
        const modalStyle = this.props.modal ? {display:"block",padding:"3em"} : {display:"none"};
        return(
            <div style={{textAlign:"left",marginBottom:"3em"}}>
                <div className="modal-main" style={modalStyle}>
                <div class="col-sm-12 text-right" onClick={this.props.handleClose}>
                                   X
                                </div>
                    <h3 id="title" class="mb-3">Starting filling here</h3>
                    <form action="#" method="post" class="">
                        <div class="blog-form">
                            <div class="row">

                                <div class="col-lg-12">
                                    <label>Name</label>
                                    <input type="text" name="name" class="form-control text " placeholder="Enter your name"/>
                                </div>
                                <div class="col-sm-12">
                                    <label>Content</label>
                                    <textarea name="Content" cols="40" rows="10" class="form-control textarea" placeholder="Enter blog content"></textarea>
                                </div>
                                <div class="col-lg-6">
                                    <label>Title</label>
                                    <input type="text" name="title" class="form-control text " placeholder="Enter the title for blog"/>
                                </div>
                                <div class="col-lg-6">
                                    <label>Genre</label>
                                    <input type="text" name="your-website" class="form-control text " placeholder="Enter the genre"/>
                                </div>
                                <div class="col-sm-12">
                                    <input type="submit" class="btn btn-primary mb-4 mb-lg-0" value="Submit"/>
                                </div>

                            </div>
                        </div>
                    </form>
                </div>
            </div>     
        );
    }
}

export default InputModal;
