import React from 'react';

class InputModal extends React.Component{
    state={
        modal:false,
        name:" ",
        email:" ",
        count:" "
    };

    toggle = () => {
        this.setState({
          modal: !this.state.modal
        });
      };

    onChangeName = (e) => {
        this.setState({
            name:e.target.value
        });
    };

    onChangeEmail = (e) => {
        this.setState({
            email:e.target.value
        });
    };

    onChangeCount = (e) => {
        this.setState({
            count:e.target.value
        });
    };

    onSubmit = (e) => {
        e.preventDefault();

        const newItem = {
            name:this.state.name,
            email:this.state.email,
            count:this.state.count
        };

        // this.props.addItem(newItem);      //calling the action

        this.toggle();
    };

    render(){
        return(
            <div style={{textAlign:"left",marginBottom:"3em"}}>
                <div>
                    <h3 id="title" class="mb-3">Starting filling here</h3>
                    <form action="#" method="post" class="">
                        <div class="blog-form">
                            <div class="row">
                                <div class="col-sm-12">
                                    <label>Comment</label>
                                    <textarea name="your-message" cols="40" rows="10" class="form-control textarea " placeholder="Comment"></textarea>
                                </div>
                                <div class="col-lg-4">
                                    <label>Your Name</label>
                                    <input type="text" name="your-name" class="form-control text " placeholder="Your Name"/>
                                </div>
                                <div class="col-lg-4">
                                    <label>Your Email</label>
                                    <input type="text" name="your-email" class="form-control text " placeholder="Your Email"/>
                                </div>
                                <div class="col-lg-4">
                                    <label>Website</label>
                                    <input type="text" name="your-website" class="form-control text " placeholder="Website"/>
                                </div>
                                <div class="col-sm-12">
                                    <input type="submit" class="btn btn-primary mb-4 mb-lg-0" value="Submit"/>
                                </div>
                            </div>
                        </div>
                    </form>
                </div>
            </div>

        );
    }
}

export default InputModal;
