import React from 'react';
import logo from './logo3.png';
export default class LoginForm extends React.Component{
    state = {
        admin:false
    }
    onSubmit = (e) => {
        e.preventDefault();


    };

    render(){
        return(
            <div className="container banner" style={{textAlign:"left",marginBottom:"3em"}}>
                <h3 id="title" className="mb-3">Enter login details</h3>
                    <div className="blog-form">
                        <div className="row">
                            <div className="col-lg-6">
                                <form onSubmit={this.onSubmit}>
                                    <label>Name</label>
                                    <input type="text" name="name" className="form-control text " placeholder="Enter your name" onChange={this.onChangeName} required/>
                                    <label>Email</label>
                                    <input type="email" name="email" className="form-control text " placeholder="Enter your email-id" onChange={this.onChangeEmail} required/>
                                    <label>Password</label>
                                    <input type="password" name="password" className="form-control text " placeholder="Enter your password" onChange={this.onChangeEmail} required/>
                                    <input type="submit" className="btn btn-primary mb-4 " value="Login"/>
                                </form>
                                <button className="btn btn-warning mb-0" style={{padding: "11px 30px"}} onClick={this.toggleAdmin}>Login as admin</button>
                            </div>
                            <div className="col-lg-6">
                            <img alt="banner"
                                 className="img-fluid"
                                 src={logo} />
                            </div>  
                        </div>
                    </div>


            </div>   

        )
    }
}
