import React from 'react'
import HeadingBanner from './HeadingBanner';
import Blog from './Blog';
import Genres from './Genres';
import pen from './pen.png'
import Footer from './Footer';
import InputModal from './InputModal';
import axios from 'axios';

class Blog_Container extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
           modal:false,
           blogs:[]
        };
    }

    componentDidMount(){
        axios
        .get(`http://localhost:5000/api`)
        .then(res=>{
            this.setState({blogs:res.data})
            console.log(res.data)
        })
    }

    showModal = () => {
        this.setState({ modal: true });
    };

    hideModal = () => {
        this.setState({ modal: false });
    };

    render() {
        return (
            <div>
                <HeadingBanner />
                <div className="container">
                    <div className="row my-5">
                        <div className="col-lg-8 col-sm-12">
                            {this.state.blogs.map((blog)=><Blog id={blog._id} genre={blog.genre} date={blog.date} author={blog.name} title={blog.title} desc={blog.content}/>)}

                        </div>

                        <div className="col-lg-4 col-sm-12">
                            <div className="widget" style={{textAlign:"center"}}>
                                <button className="start_button" onClick={this.showModal}>Start Writing...</button>
                                <img src={pen} alt=""/>
                            </div>
                            <Genres genres={["Tour","Comedy","Horror","Romance"]}/>
                        </div>
                    </div>  
                    <InputModal modal={this.state.modal} handleClose={this.hideModal}/>
                </div>

                <Footer />
            </div>

        );
    }
}

export default Blog_Container;
