import React from 'react';
import './App.css';
import BlogContainer from './components/BlogContainer';
import LoginForm from './components/LoginForm';

class App extends React.Component {
    state = {
        name:"",
        email:"",
        passsword:"",
        address:"",
        route:"login"
    }
    onChange = (e) => {
        this.setState({
            [e.target.name]:e.target.value
        });
    };

    authenticate = (admin) => {
       if(admin){
        this.setState({route:"admin"})
       }
       else{
           this.setState({route:"blog"})
       }
    }
  render(){
    return (
        <div className="App">
            {
                this.state.route==="login"?
                <LoginForm onChange={this.onChange} onSubmit={this.onSubmit} authenticate={this.authenticate}/>:
                <BlogContainer />
            }


        </div>
      );
  } 
}

export default App;
