import React from 'react';
import axios from 'axios';
class InputModal extends React.Component{
    state={
        modal:false,
        name:"",
        email:"",
        content:"",
        title:"",
        genre:"",
        genres:['DSA','Tech Talk','Motivation','Interview Experience']
    };

    toggle = () => {
        this.setState({
          modal: !this.state.modal
        });
      };

    onChangeName = (e) => {
        this.setState({
            name:e.target.value
        });
    };

    onChangeEmail = (e) => {
        this.setState({
            email:e.target.value
        });
    };

    onChangeContent = (e) => {
        this.setState({
            content:e.target.value
        });
    };

    onChangeTitle = (e) => {
        this.setState({
            title:e.target.value
        });
    };

    onChangeGenre = (e) => {
        this.setState({
            genre:e.target.value
        });
    };

    onSubmit = (e) => {
        e.preventDefault();

        const newItem = {
            name:this.state.name,
            email:this.state.email,
            content:this.state.content,
            title:this.state.title,
            genre:this.state.genre
        };

        // this.props.addItem(newItem);      //calling the action
        axios
        .post(`http://localhost:5000/api`,newItem)
        .then(res => alert(`Hi, ${this.state.name} your blog has been published`))
        .catch(err => console.log(err))

        this.props.handleClose();
    };

    render(){
        const modalStyle = this.props.modal ? {display:"block",padding:"3em"} : {display:"none"};
        return(
            <div style={{textAlign:"left",marginBottom:"3em"}}>
                <div className="modal-main" style={modalStyle}>
                    <div className="col-sm-12 text-right cp" onClick={this.props.handleClose}>
                        X
                    </div>
                    <h3 id="title" className="mb-3">Starting filling here</h3>
                    <form onSubmit={this.onSubmit}>
                        <div className="blog-form">
                            <div className="row">

                                <div className="col-lg-6">
                                    <label>Name</label>
                                    <input type="text" name="name" className="form-control text " placeholder="Enter your name" onChange={this.onChangeName} required/>
                                </div>
                                <div className="col-lg-6">
                                    <label>Email</label>
                                    <input type="email" name="email" className="form-control text " placeholder="Enter your email-id" onChange={this.onChangeEmail} required/>
                                </div>
                                <div className="col-sm-12">
                                    <label>Content</label>
                                    <textarea name="Content" cols="40" rows="10" className="form-control textarea" placeholder="Enter blog content" onChange={this.onChangeContent} required></textarea>
                                </div>
                                <div className="col-lg-6">
                                    <label>Title</label>
                                    <input type="text" name="title" className="form-control text " placeholder="Enter the title for blog" onChange={this.onChangeTitle} required/>
                                </div>
                                <div className="col-lg-6">
                                    <label>Genre</label>
                                    <select className="form-control text" name="genre"  onChange={(e)=>this.onChangeGenre(e)} required style={{width:"auto",margin:"3px"}}>
                                    <option value=""> Choose a genre</option>
                                        {
                                        this.state.genres.map((genre,index)=>(<option key={index} value={genre}>{genre}</option>))
                                        }
                                    </select>
                                </div>

                                <div className="col-sm-12">
                                    <input type="submit" className="btn btn-primary mb-4 mb-lg-0" value="Submit"/>
                                </div>

                            </div>
                        </div>
                    </form>
                </div>
            </div>     
        );
    }
}

export default InputModal;
