import React from 'react';
import axios from 'axios';
class InputModal extends React.Component{
    state={
        modal:false,
        name:" ",
        content:" ",
        title:" ",
        genre:" "
    };

    toggle = () => {
        this.setState({
          modal: !this.state.modal
        });
      };

    onChangeName = (e) => {
        this.setState({
            name:e.target.value
        });
    };

    onChangeContent = (e) => {
        this.setState({
            content:e.target.value
        });
    };

    onChangeTitle = (e) => {
        this.setState({
            title:e.target.value
        });
    };

    onChangeGenre = (e) => {
        this.setState({
            genre:e.target.value
        });
    };

    onSubmit = (e) => {
        e.preventDefault();

        const newItem = {
            name:this.state.name,
            content:this.state.content,
            title:this.state.title,
            genre:this.state.genre
        };

        // this.props.addItem(newItem);      //calling the action
        axios
        .post(`http://localhost:5000/`,newItem)
        .then(res => console.log(res))
        .catch(err => console.log(err))

        this.props.handleClose();
    };

    render(){
        const modalStyle = this.props.modal ? {display:"block",padding:"3em"} : {display:"none"};
        return(
            <div style={{textAlign:"left",marginBottom:"3em"}}>
                <div className="modal-main" style={modalStyle}>
                    <div class="col-sm-12 text-right cp" onClick={this.props.handleClose}>
                        X
                    </div>
                    <h3 id="title" class="mb-3">Starting filling here</h3>
                    <form onSubmit={this.onSubmit}>
                        <div class="blog-form">
                            <div class="row">

                                <div class="col-lg-12">
                                    <label>Name</label>
                                    <input type="text" name="name" class="form-control text " placeholder="Enter your name" onChange={this.onChangeName}/>
                                </div>
                                <div class="col-sm-12">
                                    <label>Content</label>
                                    <textarea name="Content" cols="40" rows="10" class="form-control textarea" placeholder="Enter blog content" onChange={this.onChangeContent}></textarea>
                                </div>
                                <div class="col-lg-6">
                                    <label>Title</label>
                                    <input type="text" name="title" class="form-control text " placeholder="Enter the title for blog" onChange={this.onChangeTitle}/>
                                </div>
                                <div class="col-lg-6">
                                    <label>Genre</label>
                                    <input type="text" name="your-website" class="form-control text " placeholder="Enter the genre" onChange={this.onChangeGenre}/>
                                </div>
                                <div class="col-sm-12">
                                    <input type="submit" class="btn btn-primary mb-4 mb-lg-0" value="Submit"/>
                                </div>

                            </div>
                        </div>
                    </form>
                </div>
            </div>     
        );
    }
}

export default InputModal;
